        [TestMethod]
        public void TestDataContextCommaRemoal()
        {
            var testString =
                @"{
                    ""type"": ""AdaptiveCard"",
                    ""body"": [
                      {
                          ""type"": ""Container"",
                          ""items"": [
                              {
                                  ""type"": ""TextBlock"",
                                  ""text"": ""${name}""
                              }
                          ],
                          ""$data"": ""${$root.LineItems}"" 
                          ]
                      }
                    ]
                }";
            var expectedString =
                @"{
                    ""type"": ""AdaptiveCard"",
                    ""body"": [
                    {
                        ""type"": ""Container"",
                        ""items"": [ 
                            {
                                ""type"": ""TextBlock"",
                                ""text"": ""Matt""
                            } ]
                    },
                    {
                        ""type"": ""Container"",
                        ""items"": [ 
                            {
                                ""type"": ""TextBlock"",
                                ""text"": ""David""
                            }
                        ]
                    },
                    {
                        ""type"": ""Container"",
                        ""items"": [ 
                            {
                                ""type"": ""TextBlock"",
                                ""text"": ""Thomas""
                            }
                        ]
                    }
                ]
            }";

            string jsonData = @"{
              ""LineItems"": [
                  { ""name"": ""Matt"" }, 
                  { ""name"": ""David"" }, 
                  { ""name"": ""Thomas"" }
                ]
            }";

            AdaptiveCardTemplate transformer = new AdaptiveCardTemplate(testString);
            AdaptiveCardTemplate transformer2 = new AdaptiveCardTemplate(expectedString);
            var context = new EvaluationContext()
            {
                Root = jsonData
            };

            string cardJson = transformer.Expand(context);
            string expectedcardJson = transformer2.Expand(context);
            Assert.AreEqual(expectedcardJson, cardJson);
        }

